RSpec.describe '/auction_items', type: :request do
  # This should return the minimal set of attributes required to create a valid
  # AuctionItem. As you add validations to AuctionItem, be sure to
  # adjust the attributes here as well.
  let(:user) { FactoryBot.create(:user) }
  let(:valid_attributes) do
    {
      title: 'title',
      description: 'description',
      user: user,
      game_item_id: FactoryBot.create(:game_item).id
    }
  end

  let(:invalid_attributes) do
    skip('Add a hash of attributes invalid for your model')
  end

  before do
    sign_in user
  end
  describe 'GET /index' do
    it 'renders a successful response' do
      AuctionItem.create! valid_attributes
      get auction_items_url
      expect(response).to be_successful
    end
  end

  describe 'GET /show' do
    it 'renders a successful response' do
      auction_item = AuctionItem.create! valid_attributes
      auction_item.moderation_item.approve
      get auction_item_url(auction_item)
      expect(response).to be_successful
    end
  end

  describe 'GET /new' do
    it 'renders a successful response' do
      get new_auction_item_url
      expect(response).to be_successful
    end
  end

  describe 'POST /create' do
    context 'with valid parameters' do
      it 'creates a new AuctionItem' do
        expect do
          post auction_items_url, params: { auction_item: valid_attributes }
        end.to change(AuctionItem, :count).by(1)
      end

      it 'redirects to the created auction_item' do
        post auction_items_url, params: { auction_item: valid_attributes }
        expect(response).to redirect_to(auction_item_url(AuctionItem.last))
      end
    end

    context 'with invalid parameters' do
      it 'does not create a new AuctionItem' do
        expect do
          post auction_items_url, params: { auction_item: invalid_attributes }
        end.to change(AuctionItem, :count).by(0)
      end

      it "renders a successful response (i.e. to display the 'new' template)" do
        post auction_items_url, params: { auction_item: invalid_attributes }
        expect(response).to be_successful
      end
    end
  end

  describe 'DELETE /destroy' do
    it 'destroys the requested auction_item' do
      auction_item = AuctionItem.create! valid_attributes
      expect do
        delete auction_item_url(auction_item)
      end.to change(AuctionItem, :count).by(-1)
    end

    it 'redirects to the auction_items list' do
      auction_item = AuctionItem.create! valid_attributes
      delete auction_item_url(auction_item)
      expect(response).to redirect_to(auction_items_url)
    end
  end
